import React from 'react'
import { useRef } from 'react'
import emailjs from "@emailjs/browser"
import './Contact.css'
import { useState } from 'react'

const Contact = () => {
    const ContactUs = () => {
        const form = useRef();
        const [done, detDone] = useState
        const sendEmail = (e) => {
          e.preventDefault();
      
          emailjs.sendForm('service_vdbrh16', 'template_os2read', form.current, 'YOUR_PUBLIC_KEY')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    };
  return (
    <div className="contact-form">
        <div className="s-left">
            
                <span>Get in touch</span>
                <span>Contact Me</span>
                <div className="blur c-blur1" style={{background: "#ABF1FF94"}}></div>
            
        </div>
        <div className="c-right">
            <form ref={form} onSubmit = {sendEmail}>
                <input type="text" name="user_name" className='user' placeholder='Name'/>
                <input type="email" name='user_email' className='user' placeholder='Email' />
                <textarea name="message" className='user' placeholder='Message'/>
                <input type="submit" value="send" className="button" />
                <span>{done && "Thanks for contacting me"}</span>
            </form>
        </div>
    <div className="blur c-blur2" style={{background: "var(--purple)"}}></div>
    </div>
    )
}}

export default Contact
